import {GET_ERRORS, CLEAR_ERRORS} from './types';

// RETURN ERRORS
export const returnErrors = (
  msg: string,
  status: string | number,
  id: string | null,
) => {
  return {
    type: GET_ERRORS,
    payload: {msg, status, id},
  };
};

// CLEAR ERRORS
export const clearErrors = () => {
  return {
    type: CLEAR_ERRORS,
  };
};
